import { useState, useRef, useEffect } from 'react';
import * as d3 from 'd3';
import './TradeGraph.css';

const TradeGraph = () => {
  const [data] = useState([
    [90, 20],
    [33, 80],
    [76, 42],
    [100, 36],
    [98, 29],
    [76, 98],
    [70, 84],
    [42, 78],
    [24, 120],
    [45, 60],
    [99, 68],
    [70, 35],
    [38, 24],
    [61, 46]
  ]);
  const svgRef = useRef();

  useEffect(() => {

    const w = 400;
    const h = 300;
    const svg = d3.select(svgRef.current)
      .attr('width', w)
      .attr('height', h)
      .style('overflow', 'visible')
      .style('margin-top', '100px');

      const xScale = d3.scaleLinear()
        .domian([0, 100])
        .range([0 ,w]);
      const yScale = d3.scaleLinear()
        .domian([0, 200])
        .range([h ,0]);

      const xAxis = d3.axisBottom(xScale).ticks(data.length);
      const yAxis = d3.axisLeft(yScale).ticks(10);
      svg.append('g')
        .call(xAxis)
        .attr('transform', `translate(0, ${h})`);
      svg.append('g')
        .call(yAxis);

      svg.append('text')
        .attr('x', w/2)
        .attr('y', h + 50)
        .text('x');
      svg.append('text')
        .attr('y', h/2)
        .attr('x', -50)
        .text('y');

      svg.selectAll()
        .data(data)
        .enter()
        .append('circle')
        .attr('cx', d => xScale(d[0]))
        .attr('cy', d => yScale(d[1]))
        .attr('r', 2);
  }, [data]);


  return (
    <div>
      <div className='trade-card'>
        <h1 className='trade-name'>Sales</h1>
        <h2 className='showing-trade'>Showing 2300 sales.</h2>
        <h3 className='time-trade-card'>Time:</h3>
      <div className='time-select'>
      <a href='#'><span className='time-24h'>24H</span></a>
      <a href='#'><span className='time-7D'>7D</span></a>
      <a href='#'><span className='time-30D'>30D</span></a>
      <a href='#'><span className='time-90D'>90D</span></a>
      <a href='#'><span className='time-6M'>6M</span></a>
      <a href='#'><span className='time-all'>ALL</span></a>
      </div>
      </div>

      <div className='trade-graphic'>
        <div>
          <svg ref={svgRef}></svg>
        </div>
      </div>
    </div>
);
}

export default TradeGraph;
